function formatPhoneNumber() {
  var phoneField = document.querySelector('[name="shippingAddress.phone"]');
  var phoneNumber = phoneField.value;

  if (!phoneNumber) {
    return '';
  }

  phoneNumber = phoneNumber.replace(/\s/g, '').replace(/-/g, '').replace(/\(|\)/g, '').normalize('NFD').replace(/[\u0300-\u036f]/g, '');

  if (phoneNumber.startsWith('0')) {
    phoneNumber = phoneNumber.substring(1);
  }

  if (phoneNumber.startsWith('55') || phoneNumber.startsWith('+55')) {
    phoneNumber = phoneNumber.replace(/^55(0\d+)/, '55$1');
  } else {
    phoneNumber = '55' + phoneNumber;
  }

  return phoneNumber;
}

function formatZipCode() {
  var postcodeInput = document.querySelector('[name="shippingAddress.zipcode"]');

  if (postcodeInput) {
    var postcodeValue = postcodeInput.value;

    // Extrai os primeiros 5 caracteres num√©ricos
    var zipcodeFormated = postcodeValue.match(/\d{5}/);

    if (zipcodeFormated) {

      return zipcodeFormated[0];
    } else {

      // Retorna uma string vazia se n√£o encontrar n√∫meros
      return "";
    }
  }

  return undefined;
}

